import { useState } from 'react'


const SideBar = () => {
  const [open, setOpen] = useState(true)

  return (
    <div className={`${open ? "w-72" : "w-20" } h-screen bg-violet-900 relative duration-300`}>
      <img
        src="/control.png"
        className={`absolute cursor-pointer -right-3 top-9 w-7 rounded-full border-2 border-violet-900 ${!open && "rotate-180"}`}
        onClick={() => {setOpen(!open)}}
      />
      <div className="flex gap-x-4 items-center">
        <img src="/logo-white.png" className={`w-16`}/>
      </div>
    </div>
  )
}

export default SideBar
