import { useEffect, useState } from 'react'
import { DeviceServiceClient } from './devicepb/device_grpc_web_pb'
const {DeviceRequest, DeviceResponse} = require('./devicepb/device_pb.js')

var client = new DeviceServiceClient('http://localhost:8000', null, null)


const Device = (props: any) => {
  const [status, setStatus] = useState(false)
  const { deviceId } = props

  const getStatus = () => {
    var request = new DeviceRequest()
    var stream = client.getDeviceStatus(request, {})
    stream.on('data', (response: any) => {
      setStatus(response.getStatus())
    })
  }

  useEffect(() => {
    getStatus()
  },[])

  return (
    <div className="flex w-1/4 h-[50px] bg-dark-purple p-8 items-center rounded-full">
      <div className="flex w-1/5 items-center">
        <img src="/Setting.png" className="w-50px"/>
      </div>
      <div className="flexw-4/3 flex-col gap-y-4 items-center justify-between">
          <span className="text-white">
            {deviceId}
          </span>
          <span className={`w-[30px] h-[30px] bg-[#22c55e]`}></span>
      </div>
    </div>
  )
}

export default Device
