import { useState } from 'react'


const SideBar = () => {
  const [open, setOpen] = useState(true)

  return (
    <div className={`${open ? "w-72" : "w-20" } h-screen bg-violet-900 relative`}>
      <img
        src="/control.png"
        className="absolute cursor-pointer -right-3 top-9 w-7 rounded-full border-2 border-violet-900"
        onClick={() => {setOpen(!open)}}
      />
    </div>
  )
}

export default SideBar
